select  p.products_id, p.products_model, p.products_ordered, p.products_quantity, pd.products_name, p.products_price,s.specials_new_products_price
from products p, products_description pd, categories c, categories_description cd, products_to_categories p2c, specials s where p.products_status='1' and s.products_id=p.products_id and p.products_id=pd.products_id and p.products_id=p2c.products_id and cd.categories_id=c.categories_id and p2c.categories_id=c.categories_id and pd.language_id='1' and s.status='1' and c.categories_id NOT IN (119, 117, 140, 145, 119) order by c.sort_order,p.products_sort_order